select totaltaxamount, expresstaxamount, businesstripamount from( select sum(T.amount) as totaltaxamount, test3.expresstaxamount as expresstaxamount, test3.businesstripamount as businesstripamount from etb_finance T right JOIN ( SELECT sum(F2.amount) as expresstaxamount, test2.businesstripamount as businesstripamount FROM etb_finance F2 right JOIN (SELECT sum(F.amount) as businesstripamount FROM etb_finance F where 1=1 and F.financetype='4' and F.theme='03' and F.belongto = #belongto# and F.status = '99'
F.accountdate >= #from_date#
F.accountdate <= #to_date#
) as test2 on 1=1 and F2.financetype='3' and F2.belongto = #belongto# and F2.status = '99'
F2.accountdate >= #from_date#
F2.accountdate <= #to_date#
) as test3 on 1=1 and T.belongto = #belongto# and T.status >= '20' and T.financetype='2'
T.accountdate >= #from_date#
T.accountdate <= #to_date#
)as test
